                    <div class="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-current border-r-transparent align-[-0.125em] motion-reduce:animate-[spin_1.5s_linear_infinite]" role="status">
                        <span class="!absolute !-m-px !h-px !w-px !overflow-hidden !whitespace-nowrap !border-0 !p-0 ![clip:rect(0,0,0,0)]">Loading...</span>
                    </div>
                    <div class="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent align-[-0.125em] motion-reduce:animate-[spin_1.5s_linear_infinite]" role="status">
                        <span class="!absolute !-m-px !h-px !w-px !overflow-hidden !whitespace-nowrap !border-0 !p-0 ![clip:rect(0,0,0,0)]">Loading...</span>
                    </div>
                    <div class="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-green-600 border-r-transparent align-[-0.125em] motion-reduce:animate-[spin_1.5s_linear_infinite]" role="status">
                        <span class="!absolute !-m-px !h-px !w-px !overflow-hidden !whitespace-nowrap !border-0 !p-0 ![clip:rect(0,0,0,0)]">Loading...</span>
                    </div>
                </div>
            </div>

            <!-- Pulse Animation -->
            <div class="mb-8">
                <h2 class="text-xl font-semibold text-gray-900 dark:text-white mb-4">Pulse Animation</h2>
                <div class="flex items-center space-x-4">
                    <div class="inline-block h-4 w-4 animate-pulse rounded-full bg-blue-600"></div>
                    <div class="inline-block h-4 w-4 animate-pulse rounded-full bg-green-600"></div>
                    <div class="inline-block h-4 w-4 animate-pulse rounded-full bg-red-600"></div>
                    <div class="inline-block h-4 w-4 animate-pulse rounded-full bg-yellow-600"></div>
                </div>
            </div>

            <!-- Bounce Animation -->
            <div class="mb-8">
                <h2 class="text-xl font-semibold text-gray-900 dark:text-white mb-4">Bounce Animation</h2>
                <div class="flex items-center space-x-2">
                    <div class="inline-block h-4 w-4 animate-bounce rounded-full bg-blue-600"></div>
                    <div class="inline-block h-4 w-4 animate-bounce rounded-full bg-blue-600" style="animation-delay: 0.1s"></div>
                    <div class="inline-block h-4 w-4 animate-bounce rounded-full bg-blue-600" style="animation-delay: 0.2s"></div>
                </div>
            </div>

            <!-- Loading Overlay -->
            <div class="mb-8">
                <h2 class="text-xl font-semibold text-gray-900 dark:text-white mb-4">Loading Overlay</h2>
                <div class="relative">
                    <div class="p-6 bg-gray-100 rounded-lg dark:bg-gray-800">
                        <h3 class="text-lg font-semibold text-gray-900 dark:text-white">Content Area</h3>
